mapper.delete:
delete from tbl_activity
where id in

    #{id}

mapper.select:
select a.id,a.name,u.name as owner,a.startDate,a.endDate
from tbl_activity a
join tbl_user u
on a.owner=u.id


     a.name like '%' #{name} '%'


    and u.name like '%' #{owner} '%'


    and startDate > #{startTime}


    and endDate < #{endTime}



order by a.createTime desc
limit #{skipCount},#{PageSize}

select count(*) from tbl_activity a join tbl_user u on a.owner=u.id
a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and startDate > #{startTime}
and endDate < #{endTime}
